import React, { useState }  from 'react'
import axios from "axios";
import * as yup from "yup";

export default function Form(props) {

    const initialFormValues = {
        name: "",
        email: "",
        password: "",
        tos: false
      }
    const [form, setForm] = useState(initialFormValues)

    const [disabled, setDisabled] = useState(true)

    const onChange = (event) => {
        const { name, type, value, checked } = event.target
        const valueToUse = type === "checkbox" ? checked : value
        setForm({ ...form, [name]: valueToUse })
        console.log("changing!")
      } 

    const submit = (event) => {
    event.preventDefault()
    const newUser = {
        name: form.name.trim(),
        email: form.email.trim(),
        password: form.password.trim(),
        tos: form.tos
    }
    axios
        .post("https://reqres.in/api/users", newUser)
        .then((res) => {
        setForm({ user: "", ageRange: "", state: "", isGoing: false })
        })
        .catch((err) => {
        debugger;
        })
    }


    return (
        <form className='form-container' onSubmit={submit}>
        <div classNAme='form-group'>
          <label>
            <input placeholder='Name' type='text' name='name' onChange={onChange} values={form.name} />
          </label>
          <label>
            <input placeholder='Email' type='text' name='email' onChange={onChange} values={form.email} />
          </label>
          <label>
            <input placeholder='Password' type='text' name='password' onChange={onChange} values={form.password} />
          </label>
          <label>
              Terms of Service: 
              <input name="tos" type="checkbox" checked={form.tos} />
          </label>
          <div className='submit-container'>
            <button>Submit</button>
          </div>
        </div>
     </form>
    )
}
